import React from 'react';
import { useState,useEffect } from 'react';
import {useDispatch, useSelector} from 'react-redux';
import {getAllCountries} from '../actions';
import {Link} from 'react-router-dom'
import Card from './Card';
import Paged from './Paged';


export default function Home(){
    const dispatch = useDispatch();
    const allCountries = useSelector((state) => state.allCountries)
    const [currentPage,setCurrentPage] = useState(1)
    const [countriesPerPage, setCountriesPerPage] = useState(10) 
    const indexOfLastCountry = currentPage * countriesPerPage
    const indexOfFirstCountry = indexOfLastCountry - countriesPerPage
    const currentCountries = allCountries.slice(indexOfFirstCountry,indexOfLastCountry)

    const totalPages= (pageNumber) => {
        setCurrentPage(pageNumber)
    }

    useEffect (() => {
        dispatch(getAllCountries());
    }, [dispatch]);

    const handleOnClickReset = (e) => {
        e.preventDefault();
        dispatch(getAllCountries());
    }

    return (
    <div className="home-order">
        <Link to='/activities'>Crear actividad</Link>
        <h1>Bienvenidos a PAISES</h1>
        <div>
                <button onClick={handleOnClickReset}>Reset</button>
        </div>
        <div>
           <select>
               <option value={"asc"}>Ascendente</option>
               <option value={"desc"}>Descendente</option>
           </select>
        </div>
        <div>
        <span>Filter By Continent: </span>
        <select >
            <option value={"All"}>-- All --</option>
            <option value={"Europe"}>-- Europe --</option>
            <option value={"Asia"}>-- Asia --</option>
            <option value={"Oceania"}>-- Oceania --</option>
            <option value={"Africa"}>-- Africa --</option> 
            <option value={"Antarctic"}>-- Antarctic --</option> 
            <option value={"Americas"}>-- Americas --</option> 
        </select>
        </div>
       
          <div div className="cards-container">
                {
                    allCountries?.length 
                    ?
                    allCountries?.map(co => {
                        return <Card flag={co.flag} name={co.name} continent={co.continent} id={co.id} key={co.id}/>
                    })
                    :
                    <div>Loading...</div> 
                }
            </div>
          
            <div>
                <Paged
                countriesPerPage = {countriesPerPage}
                allCountries = {allCountries.length}
                totalPages = {totalPages}
                />
            </div>
        
        
        
    </div>
        

        )
}
