import React from 'react'
import {GET_ALL_COUNTRIES, FILTER_BY_CONTINENT, ORDER_BY_NAME} from "../actions"

const initialState={
  countries: [],
  allCountries: []
}

const rootReducer = (state = initialState, action) => {
  switch (action.type){
    case GET_ALL_COUNTRIES: 
    return{
      ...state,
      countries: action.payload,
      allCountries: action.payload
    }
    case FILTER_BY_CONTINENT:
      const allCountries = state.allCountries;
      const continentFiltered = action.payload === "All" ? allCountries : allCountries.filter(con => con.continent === action.payload)
      return{
        ...state,
        countries: continentFiltered
    }

      case ORDER_BY_NAME:
        let sortedArr = action.payload === 'asc'?
           state.countries.sort(function(a,b){
            if(a.name > b.name){
              return 1;
            }
            if(b.name > a.name){
              return -1;
            }
            return 0;
           }) :
           state.countries.sort(function(a,b){ //descendente
            if(a.name > b.name){
              return -1;
            }
            if(b.name > a.name){
              return 1;
            }
            return 0;
           })
           return{
            ...state,
            countries: sortedArr
           }


    default: return state;
  }
}

export default rootReducer
