import React from 'react'

const Paged = ({ countriesPerPage, allCountries, totalPages }) => {
    const pageNumber = [];
    //Math.ceil redondea todos mis países, sobre los países que quiero por página
    for (let i = 1; i <= Math.ceil(allCountries/countriesPerPage); i++) {
        //Push a mi arreglo vacio para que tome el valor de esa itreción
        pageNumber.push(i)
    }

  return (
    <>
    <ul className="pageNumbers">
        {pageNumber?.map(el =>(
            <li key={el}>
                <a onClick={()=> totalPages(el)}>{el}</a>
            </li>
        ))}
    </ul>
</>
  )
}

export default Paged;
