import React from 'react';
import { useState,useEffect } from 'react';
import {useDispatch, useSelector} from 'react-redux';
import {getAllCountries, FilterByContinent, orderByName} from '../actions';
import {Link} from 'react-router-dom'
import Card from './Card';
import Paged from './Paged';


export default function Home(){
    const dispatch = useDispatch();
    const allCountries = useSelector((state) => state.countries)
    const [orden,setOrden] = us
    const [currentPage,setCurrentPage] = useState(1)
    const [countriesPerPage, setCountriesPerPage] = useState(9) 
    const indexOfLastCountry = currentPage * countriesPerPage
    const indexOfFirstCountry = indexOfLastCountry - countriesPerPage
    const currentCountries = allCountries.slice(indexOfFirstCountry,indexOfLastCountry)

    const totalPages= (pageNumber) => {
        setCurrentPage(pageNumber)
    }

    useEffect (() => {
        dispatch(getAllCountries());
    }, [dispatch]);

    const handleOnClickReset = (e) => {
        e.preventDefault();
        dispatch(getAllCountries());
    }

    function handleFilterContinent(e){
       dispatch(FilterByContinent(e.target.value))
    }

    function handleSort(e){
        e.preventDefault();
        dispatch(orderByName(e.target.value))
        setCurrentPage(1);
        setOrden(``)
    }

    return (
    <div className="home-order">
        <Link to='/activities'>Crear actividad</Link>
        <h1>Bienvenidos a PAISES</h1>
        <div>
                <button onClick={handleOnClickReset}>Reset</button>
        </div>
        <div>
           <select onChange={e => handleSort(e)}>
               <option value={"asc"}>Ascendente</option>
               <option value={"desc"}>Descendente</option>
           </select>
        </div>
        <div>
        <span>Filter By Continent: </span>
        <select onChange={e => handleFilterContinent(e)}>
            <option value={"All"}>-- All --</option>
            <option value={"Europe"}>-- Europe --</option>
            <option value={"Asia"}>-- Asia --</option>
            <option value={"Oceania"}>-- Oceania --</option>
            <option value={"Africa"}>-- Africa --</option> 
            <option value={"Antarctic"}>-- Antarctic --</option> 
            <option value={"Americas"}>-- Americas --</option> 
        </select>
        </div>
        <div>
             <Paged
                countriesPerPage = {countriesPerPage}
                allCountries = {allCountries.length}
                totalPages = {totalPages}
                />
         </div>
            
          <div div className="cards-container">
                {
                    currentCountries?.length 
                    ?
                    currentCountries?.map(co => {
                        return (
                        <Link to={"/home/" +co.id}>
                        <Card flag={co.flag} name={co.name} continent={co.continent} id={co.id} key={co.id}/>
                        </Link>
                    )})
                    :
                    <div>Loading...</div> 
                }
            </div>
            
        
        
        
    </div>
        

        )
}
